$(document).ready(function(){$(".slider").each(function(){var images=$(this).find("img"),duration=$(this).data("duration"),interval=images.length*duration,fade=800;if(images.length>1){images.not(":first").hide();var cycle=function(){images.not(":first").not(":last").hide();images.last().fadeOut(fade);images.each(function(i){var $this=$(this),d=i*duration;setTimeout(function(){$this.fadeIn(fade)},d)})};cycle();setInterval(function(){cycle()},interval)}})});